        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/CertificateNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title CertificateNFT
 * @dev ERC721 NFT for verified academic certificates.
 *      Only the admin (owner) can mint certificates.
 *      Metadata (title, description, image) is stored on IPFS; only CID is stored on-chain.
 */
contract CertificateNFT is ERC721, ERC721URIStorage, Ownable, ReentrancyGuard, Pausable {
    uint256 private _nextTokenId;

    // Events
    event CertificateIssued(uint256 indexed tokenId, address indexed student, string uri, string title);
    event CertificateRevoked(uint256 indexed tokenId);

    // Certificate metadata stored on-chain for quick lookups
    struct CertificateData {
        string title;
        string category;
        address student;
        uint256 issuedAt;
        bool revoked;
    }

    mapping(uint256 => CertificateData) public certificates;

    constructor() ERC721("Learn2Earn Certificate", "L2ECERT") Ownable(msg.sender) {}

    /**
     * @dev Mint a new certificate NFT to a student.
     * @param student The student's wallet address
     * @param uri IPFS URI for the certificate metadata
     * @param title Certificate title
     * @param category Certificate category (e.g., "Course Completion", "Achievement")
     */
    function safeMint(
        address student,
        string calldata uri,
        string calldata title,
        string calldata category
    ) external onlyOwner whenNotPaused nonReentrant returns (uint256) {
        require(student != address(0), "CertificateNFT: mint to zero address");

        uint256 tokenId = _nextTokenId++;
        _safeMint(student, tokenId);
        _setTokenURI(tokenId, uri);

        certificates[tokenId] = CertificateData({
            title: title,
            category: category,
            student: student,
            issuedAt: block.timestamp,
            revoked: false
        });

        emit CertificateIssued(tokenId, student, uri, title);
        return tokenId;
    }

    /**
     * @dev Revoke a certificate (marks as revoked, does NOT burn).
     * @param tokenId The certificate token ID
     */
    function revokeCertificate(uint256 tokenId) external onlyOwner {
        require(tokenId < _nextTokenId, "CertificateNFT: token does not exist");
        certificates[tokenId].revoked = true;
        emit CertificateRevoked(tokenId);
    }

    /**
     * @dev Get the total number of minted certificates.
     */
    function totalSupply() external view returns (uint256) {
        return _nextTokenId;
    }

    /**
     * @dev Get certificate data by token ID.
     */
    function getCertificate(uint256 tokenId) external view returns (CertificateData memory) {
        require(tokenId < _nextTokenId, "CertificateNFT: token does not exist");
        return certificates[tokenId];
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }

    // ─── Required Overrides ─────────────────────────────

    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/CourseMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

interface IERC20 {
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function transfer(address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title CourseMarketplace
 * @dev Handles course enrollment using EDU tokens.
 *      Admin creates courses with a price. Students approve & pay EDU tokens to enroll.
 */
contract CourseMarketplace is Ownable, ReentrancyGuard, Pausable {
    IERC20 public eduToken;

    struct Course {
        uint256 id;
        string name;
        uint256 price;      // in EDU (wei)
        uint256 spots;       // max spots (0 = unlimited)
        uint256 enrolled;    // current enrollments
        bool active;
    }

    uint256 public nextCourseId;
    mapping(uint256 => Course) public courses;
    mapping(uint256 => mapping(address => bool)) public enrollments; // courseId => student => enrolled

    // Revenue tracking
    uint256 public totalRevenue;

    // Events
    event CourseCreated(uint256 indexed courseId, string name, uint256 price, uint256 spots);
    event CourseUpdated(uint256 indexed courseId, uint256 price, bool active);
    event StudentEnrolled(uint256 indexed courseId, address indexed student, uint256 price);
    event RevenueWithdrawn(address indexed to, uint256 amount);

    constructor(address _eduToken) Ownable(msg.sender) {
        require(_eduToken != address(0), "Marketplace: invalid token address");
        eduToken = IERC20(_eduToken);
    }

    /**
     * @dev Admin creates a new course.
     */
    function createCourse(string calldata name, uint256 price, uint256 spots) external onlyOwner whenNotPaused {
        require(price > 0, "Marketplace: price must be > 0");

        uint256 courseId = nextCourseId++;
        courses[courseId] = Course({
            id: courseId,
            name: name,
            price: price,
            spots: spots,
            enrolled: 0,
            active: true
        });

        emit CourseCreated(courseId, name, price, spots);
    }

    /**
     * @dev Admin updates course price or active status.
     */
    function updateCourse(uint256 courseId, uint256 price, bool active) external onlyOwner {
        require(courseId < nextCourseId, "Marketplace: course does not exist");
        courses[courseId].price = price;
        courses[courseId].active = active;
        emit CourseUpdated(courseId, price, active);
    }

    /**
     * @dev Student enrolls in a course by paying EDU tokens.
     *      Student must approve this contract to spend their tokens first.
     */
    function enroll(uint256 courseId) external whenNotPaused nonReentrant {
        Course storage course = courses[courseId];

        require(courseId < nextCourseId, "Marketplace: course does not exist");
        require(course.active, "Marketplace: course not active");
        require(!enrollments[courseId][msg.sender], "Marketplace: already enrolled");
        require(course.spots == 0 || course.enrolled < course.spots, "Marketplace: course full");

        // Transfer tokens from student to this contract
        bool success = eduToken.transferFrom(msg.sender, address(this), course.price);
        require(success, "Marketplace: token transfer failed");

        enrollments[courseId][msg.sender] = true;
        course.enrolled++;
        totalRevenue += course.price;

        emit StudentEnrolled(courseId, msg.sender, course.price);
    }

    /**
     * @dev Check if a student is enrolled in a course.
     */
    function isEnrolled(uint256 courseId, address student) external view returns (bool) {
        return enrollments[courseId][student];
    }

    /**
     * @dev Get course details.
     */
    function getCourse(uint256 courseId) external view returns (Course memory) {
        require(courseId < nextCourseId, "Marketplace: course does not exist");
        return courses[courseId];
    }

    /**
     * @dev Get total number of courses.
     */
    function getCourseCount() external view returns (uint256) {
        return nextCourseId;
    }

    /**
     * @dev Admin withdraws accumulated revenue.
     */
    function withdrawRevenue(address to) external onlyOwner nonReentrant {
        uint256 balance = eduToken.balanceOf(address(this));
        require(balance > 0, "Marketplace: no revenue to withdraw");
        bool success = eduToken.transfer(to, balance);
        require(success, "Marketplace: withdrawal failed");
        emit RevenueWithdrawn(to, balance);
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }
}

contracts/EDUToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Capped.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title EDUToken
 * @dev Learn2Earn ERC20 Token with capped supply, burnable, pausable, and admin-mint functionality.
 *      Owner (admin) starts with 1,000,000 EDU. Max supply is 100,000,000 EDU.
 */
contract EDUToken is ERC20, ERC20Burnable, ERC20Capped, Ownable, ReentrancyGuard, Pausable {
    // Events
    event TokensMinted(address indexed to, uint256 amount, string reason);
    event TokensBurned(address indexed from, uint256 amount);
    event RewardSent(address indexed admin, address indexed student, uint256 amount, string reason);

    /**
     * @dev Constructor mints 1,000,000 EDU tokens to the deployer (admin).
     *      Max cap is 100,000,000 EDU.
     */
    constructor()
        ERC20("Learn2Earn", "EDU")
        ERC20Capped(100_000_000 * 10 ** 18)
        Ownable(msg.sender)
    {
        // Mint initial 1M tokens to admin
        _mint(msg.sender, 1_000_000 * 10 ** 18);
    }

    /**
     * @dev Admin-only function to mint tokens to a student wallet.
     * @param to Recipient address
     * @param amount Token amount (in wei)
     * @param reason Description of why the tokens are minted
     */
    function mint(address to, uint256 amount, string calldata reason) external onlyOwner whenNotPaused nonReentrant {
        require(to != address(0), "EDU: mint to zero address");
        require(amount > 0, "EDU: amount must be > 0");
        _mint(to, amount);
        emit TokensMinted(to, amount, reason);
    }

    /**
     * @dev Admin sends reward tokens to a student from admin balance.
     * @param student Student wallet address
     * @param amount Token amount (in wei)
     * @param reason Description for the reward
     */
    function rewardStudent(address student, uint256 amount, string calldata reason) external onlyOwner whenNotPaused nonReentrant {
        require(student != address(0), "EDU: reward to zero address");
        require(amount > 0, "EDU: amount must be > 0");
        require(balanceOf(msg.sender) >= amount, "EDU: insufficient admin balance");
        _transfer(msg.sender, student, amount);
        emit RewardSent(msg.sender, student, amount, reason);
    }

    /**
     * @dev Pause token operations. Only owner.
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token operations. Only owner.
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // ─── Required Overrides ─────────────────────────────

    function _update(address from, address to, uint256 value)
        internal
        override(ERC20, ERC20Capped)
    {
        super._update(from, to, value);
    }
}

contracts/HackathonPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

interface IERC20 {
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function transfer(address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title HackathonPool
 * @dev Manages hackathon registrations with EDU token entry fees and prize distribution.
 */
contract HackathonPool is Ownable, ReentrancyGuard, Pausable {
    IERC20 public eduToken;

    struct Hackathon {
        uint256 id;
        string name;
        uint256 entryFee;        // in EDU (wei)
        uint256 maxParticipants;
        uint256 participantCount;
        bool active;
        bool rewardsDistributed;
    }

    uint256 public nextHackathonId;
    mapping(uint256 => Hackathon) public hackathons;
    mapping(uint256 => mapping(address => bool)) public registrations;
    mapping(uint256 => address[]) public participantLists;

    // Events
    event HackathonCreated(uint256 indexed hackathonId, string name, uint256 entryFee, uint256 maxParticipants);
    event StudentRegistered(uint256 indexed hackathonId, address indexed student, uint256 entryFee);
    event RewardsDistributed(uint256 indexed hackathonId, address[] winners, uint256[] amounts);

    constructor(address _eduToken) Ownable(msg.sender) {
        require(_eduToken != address(0), "HackathonPool: invalid token");
        eduToken = IERC20(_eduToken);
    }

    /**
     * @dev Admin creates a hackathon.
     */
    function createHackathon(string calldata name, uint256 entryFee, uint256 maxParticipants) external onlyOwner whenNotPaused {
        uint256 hackathonId = nextHackathonId++;
        hackathons[hackathonId] = Hackathon({
            id: hackathonId,
            name: name,
            entryFee: entryFee,
            maxParticipants: maxParticipants,
            participantCount: 0,
            active: true,
            rewardsDistributed: false
        });
        emit HackathonCreated(hackathonId, name, entryFee, maxParticipants);
    }

    /**
     * @dev Student registers for a hackathon by paying the entry fee.
     */
    function register(uint256 hackathonId) external whenNotPaused nonReentrant {
        Hackathon storage h = hackathons[hackathonId];
        require(hackathonId < nextHackathonId, "HackathonPool: does not exist");
        require(h.active, "HackathonPool: not active");
        require(!registrations[hackathonId][msg.sender], "HackathonPool: already registered");
        require(h.maxParticipants == 0 || h.participantCount < h.maxParticipants, "HackathonPool: full");

        if (h.entryFee > 0) {
            bool success = eduToken.transferFrom(msg.sender, address(this), h.entryFee);
            require(success, "HackathonPool: payment failed");
        }

        registrations[hackathonId][msg.sender] = true;
        participantLists[hackathonId].push(msg.sender);
        h.participantCount++;

        emit StudentRegistered(hackathonId, msg.sender, h.entryFee);
    }

    /**
     * @dev Admin distributes prize rewards to winners.
     */
    function distributeRewards(
        uint256 hackathonId,
        address[] calldata winners,
        uint256[] calldata amounts
    ) external onlyOwner nonReentrant {
        require(hackathonId < nextHackathonId, "HackathonPool: does not exist");
        require(winners.length == amounts.length, "HackathonPool: length mismatch");
        require(!hackathons[hackathonId].rewardsDistributed, "HackathonPool: already distributed");

        for (uint256 i = 0; i < winners.length; i++) {
            require(winners[i] != address(0), "HackathonPool: zero address winner");
            bool success = eduToken.transfer(winners[i], amounts[i]);
            require(success, "HackathonPool: transfer failed");
        }

        hackathons[hackathonId].rewardsDistributed = true;
        emit RewardsDistributed(hackathonId, winners, amounts);
    }

    /**
     * @dev Check if registered.
     */
    function isRegistered(uint256 hackathonId, address student) external view returns (bool) {
        return registrations[hackathonId][student];
    }

    /**
     * @dev Get participant list.
     */
    function getParticipants(uint256 hackathonId) external view returns (address[] memory) {
        return participantLists[hackathonId];
    }

    /**
     * @dev Get hackathon count.
     */
    function getHackathonCount() external view returns (uint256) {
        return nextHackathonId;
    }

    /**
     * @dev Deactivate a hackathon.
     */
    function deactivateHackathon(uint256 hackathonId) external onlyOwner {
        require(hackathonId < nextHackathonId, "HackathonPool: does not exist");
        hackathons[hackathonId].active = false;
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }
}

contracts/TaskController.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

/**
 * @title TaskController
 * @dev Manages tasks and automatic verification for Learn2Earn.
 *      Handles conditional verification and automatic token distribution.
 */
contract TaskController is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;

    IERC20 public eduToken;

    enum VerificationType {
        INSTANT,        // No condition, just claim (for testing/simple tasks)
        TOKEN_BALANCE,  // User must hold >= X amount of tokens
        SIGNATURE,      // Off-chain verification (Quiz, File, etc) signed by admin
        EXTERNAL_CONTRACT // Check external contract state (placeholder)
    }

    struct Task {
        uint256 id;
        string title;
        string condition;   // New: Condition string (e.g. "Attendance > 90%")
        uint256 reward;
        VerificationType verificationType;
        uint256 minTokenBalance;
        bool isActive;
    }

    // Task ID -> Task
    mapping(uint256 => Task) public tasks;
    uint256 public nextTaskId;

    // Task ID -> User -> Completed
    mapping(uint256 => mapping(address => bool)) public hasCompletedTask;

    event TaskCreated(uint256 indexed taskId, string title, string condition, uint256 reward, VerificationType verificationType);
    event TaskUpdated(uint256 indexed taskId, bool isActive);
    event TaskVerified(uint256 indexed taskId, address indexed student, uint256 reward);

    constructor(address _eduToken) Ownable(msg.sender) {
        require(_eduToken != address(0), "Invalid token address");
        eduToken = IERC20(_eduToken);
        nextTaskId = 1;
    }

    /**
     * @dev Admin creates a new task.
     */
    function createTask(
        string calldata _title,
        string calldata _condition,
        uint256 _reward,
        VerificationType _verificationType,
        uint256 _minTokenBalance
    ) external onlyOwner {
        tasks[nextTaskId] = Task({
            id: nextTaskId,
            title: _title,
            condition: _condition,
            reward: _reward,
            verificationType: _verificationType,
            minTokenBalance: _minTokenBalance,
            isActive: true
        });

        emit TaskCreated(nextTaskId, _title, _condition, _reward, _verificationType);
        nextTaskId++;
    }

    /**
     * @dev Admin updates task status.
     */
    function setTaskStatus(uint256 _taskId, bool _isActive) external onlyOwner {
        require(tasks[_taskId].id != 0, "Task does not exist");
        tasks[_taskId].isActive = _isActive;
        emit TaskUpdated(_taskId, _isActive);
    }

    /**
     * @dev Student verifies task completion and claims reward.
     * @param _taskId Task ID to verify
     * @param _signature Signature from admin (if required), or empty bytes
     */
    function verifyTask(uint256 _taskId, bytes calldata _signature) external nonReentrant {
        Task memory task = tasks[_taskId];

        require(task.id != 0, "Task does not exist");
        require(task.isActive, "Task is not active");
        require(!hasCompletedTask[_taskId][msg.sender], "Task already completed");

        // Verify based on type
        if (task.verificationType == VerificationType.INSTANT) {
            // No check needed
        } 
        else if (task.verificationType == VerificationType.TOKEN_BALANCE) {
            require(eduToken.balanceOf(msg.sender) >= task.minTokenBalance, "Insufficient token balance");
        } 
        else if (task.verificationType == VerificationType.SIGNATURE) {
            // Reconstruct the signed message: hash(taskId, studentAddress)
            bytes32 messageHash = keccak256(abi.encodePacked(_taskId, msg.sender));
            bytes32 ethSignedMessageHash = MessageHashUtils.toEthSignedMessageHash(messageHash);

            address signer = ECDSA.recover(ethSignedMessageHash, _signature);
            require(signer == owner(), "Invalid signature");
        }
        else {
            revert("Unsupported verification type");
        }

        // Mark as completed
        hasCompletedTask[_taskId][msg.sender] = true;

        // Distribute Reward
        // Transfer from Admin (Owner) to Student
        // Requires Admin to have approved this contract!
        require(eduToken.transferFrom(owner(), msg.sender, task.reward), "Transfer failed. Admin/Contract allowance?");

        emit TaskVerified(_taskId, msg.sender, task.reward);
    }

    /**
     * @dev Check if a user has completed a task.
     */
    function isTaskCompleted(uint256 _taskId, address _user) external view returns (bool) {
        return hasCompletedTask[_taskId][_user];
    }
}

